import React, { useState } from 'react';

import { TextField, Checkbox } from '@material-ui/core';
import { RadioButtonsGroup } from './RadioGroup';

export const Form = () => {
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    gender: 'female',
    checked: true
  });

  const [checked, setChecked] = useState(true);
  // const [radioChecked, setRadioChecked] = useState('female');

  const handleChange = event => {
    const {name, value} = event.target;
    setValues({
      ...values,
    [name]: value
    })
  };

  const handleCheck = event => setChecked(event.target.checked);

  // const handleRadioCheck = event => {
  //   console.log(event);
  //   setRadioChecked(!radioChecked)
  // };

  return (
    <form noValidate>
      <div className="form">
        <TextField
          required
          name="name"
          label="Name"
          autoComplete="off"
          // helperText="Some important text"
          variant="outlined"
          value={values.name}
          onChange={handleChange}
        />
        <RadioButtonsGroup
          value={value}
          onChange={handleChange}
        />
        <TextField
          required
          name="email"
          label="Email"
          autoComplete="off"
          // helperText="Some important text"
          variant="outlined"
          value={values.email}
          onChange={handleChange}
        />
        <TextField
          required
          name="password"
          label="Password"
          autoComplete="off"
          type="password"
          variant="outlined"
          value={values.password}
          onChange={handleChange}
        />
        <div className="form-checkbox">
          <Checkbox
            checked={checked}
            onChange={handleCheck}
            value="secondary"
            color="primary"
          />
          <p>A checkbox is a GUI widget that permits the user to make a binary choice</p>
        </div>
      </div>
    </form>
  );
};
