import React, {Fragment} from 'react';
import {useFormik} from 'formik';
import {validationSchema} from '../validation/validationSchema';

import {Button, TextField, FormControlLabel, Checkbox} from "@material-ui/core";

export const FormikForm = () => {
  const {handleChange, handleSubmit, values, errors} = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      checked: true
    },
    validationSchema,
    onSubmit(values) {
      console.log(values)
    }
  });

  // const [state, setState] = useState({
  //   offers: false,
  //   news: false
  // });
  //
  // const handleChange = name => event => {
  //   setState({ ...state, [name]: event.target.checked });
  // };
  //
  // const { offers, news } = state;


  return (
    <Fragment>
      <h1 className="page-title">Form Validation with Formik</h1>
      <form onSubmit={handleSubmit} noValidate className="form">
        <TextField
          required
          onChange={handleChange}
          values={values.name}
          {...errors.name && {helperText: errors.name, error: true}}
          name="name"
          label="Name"
          autoComplete="off"
          variant="outlined"
        />
          <TextField
            required
            onChange={handleChange}
            values={values.email}
            {...errors.email && {helperText: errors.email, error: true}}
            name="email"
            label="Email"
            autoComplete="off"
            variant="outlined"
          />
        <TextField
          required
          onChange={handleChange}
          values={values.email}
          {...errors.password && {helperText: errors.password, error: true}}
          name="password"
          label="Password"
          autoComplete="off"
          variant="outlined"
        />
        {/*<FormControlLabel*/}
        {/*  control={<Checkbox*/}
        {/*    checked={values.checked}*/}
        {/*    onChange={handleChange("consent")}*/}
        {/*    value="checkedA"*/}
        {/*    name="consent"*/}
        {/*    type="checkbox"*/}
        {/*  />} label="Secondary"*/}
        {/*/>*/}

        <label className="form-field" htmlFor="consent">
          <span>Consent:</span>
          <input
            name="consent"
            type="checkbox"
            onChange={handleChange}
          />
        </label>
          <Button type="submit" variant="contained">SUBMIT</Button>
    </form>
    </Fragment>
  )
};
