import React, {useState} from 'react';
import {FormControl, FormGroup, FormControlLabel, Checkbox} from '@material-ui/core';

export const CheckboxesGroup = ({ values, onChange }) => {
  // const [state, setState] = useState({
  //   offers: false,
  //   news: false
  // });
  //
  // const handleChange = name => event => {
  //   setState({ ...state, [name]: event.target.checked });
  // };
  //
  // const { offers, news } = state;

  return (
    <div className="checkbox-container">
      <FormControl component="fieldset">
        <FormGroup>
          <FormControlLabel
            control={<Checkbox values={values} onChange={onChange('offers')} value="offers" />}
            label="Do you want to know about our special offers?"
          />
          <FormControlLabel
            control={<Checkbox values={values} onChange={onChange('news')} value="news" />}
            label="What about news from us?"
          />
        </FormGroup>
      </FormControl>
    </div>
  );
};
