import React from 'react';
import {Formik, Field} from 'formik';
import {Button, TextField, FormControlLabel, Checkbox, Radio} from "@material-ui/core";

export const FormikForm2 = () => {
  return (
    <div>
      <Formik initialValues={{name: "", email: ""}} onSubmit={(data, {setSubmitting}) => {
        setSubmitting(true);
        //async call
        console.log(data);
        setSubmitting(false);
      }}
      >
        {({values, handleChange, handleSubmit, isSubmitting}) => (
          <form onSubmit={handleSubmit} className="form">
            <Field name="name" type="input" autocomplete="off" variant="outlined" label="Name" as={TextField}/>
            <Field name="email" type="input" autocomplete="off" variant="outlined" label="Email" as={TextField}/>

            <Button type="submit" variant="contained">SUBMIT</Button>
            <pre>
              {JSON.stringify(values, null, 2)}
            </pre>
          </form>
        )}
      </Formik>
    </div>
  )
};
