import React, {Fragment} from 'react';
import {useFormik} from 'formik';
import {validationSchema} from '../validation/validationSchema';

import {Button, TextField, FormControlLabel, Checkbox} from "@material-ui/core";

export const FormikForm = () => {
  const {handleChange, handleSubmit, values, errors} = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      isEnabled: true,
    },
    validationSchema,
    onSubmit(values) {
      console.log(values)
    }
  });

  return (
    <Fragment>
      <h1 className="page-title">Form Validation with Formik</h1>
      <form onSubmit={handleSubmit} noValidate className="form">
        <TextField
          required
          onChange={handleChange}
          values={values.name}

          name="name"
          label="Name"
          autoComplete="off"
          variant="outlined"
        />
        {errors.name && <div className="form-field-error">{errors.name}</div>}
        <TextField
          required
          onChange={handleChange}
          values={values.email}
          name="email"
          label="Email"
          autoComplete="off"
          variant="outlined"
        />
        {errors.email && <div className="form-field-error">{errors.email}</div>}
        <TextField
          required
          onChange={handleChange}
          values={values.password}

          name="password"
          label="Password"
          autoComplete="off"
          variant="outlined"
        />
        {errors.password && <div className="form-field-error">{errors.password}</div>}
        <FormControlLabel
          control={<Checkbox
            type="checkbox"
            checked={values.isEnabled}
            onChange={handleChange("isEnabled")}
            name="isEnabled"
          />} label="This is a consent"
        />
        {errors.isEnabled && <div className="form-field-error">{errors.isEnabled}</div>}
        <Button type="submit" variant="contained">SUBMIT</Button>
      </form>
    </Fragment>
  )
};
