import React from 'react';
import {Formik, Field, Form} from 'formik';
import {Button, TextField, Checkbox, Radio} from "@material-ui/core";

export const FormikForm2 = () => {
  return (
    <div>
      <Formik initialValues={{
        name: "",
        email: "",
        password: "",
        consent: true,
        register: '',

      }} onSubmit={(data, {setSubmitting}) => {
        setSubmitting(true);
        //async call
        console.log(data);
        setSubmitting(false);
      }}
      >
        {({values, isSubmitting}) => (
          <Form className="form">
            <Field
              name="name"
              type="input"
              autoComplete="off"
              variant="outlined"
              label="Name"
              as={TextField}
            />
            <Field
              name="email"
              type="input"
              autoComplete="off"
              variant="outlined"
              label="Email"
              as={TextField}
            />
            <Field
              name="password"
              type="password"
              autoComplete="off"
              variant="outlined"
              label="Password"
              as={TextField}
            />
            <div className="single-checkbox-wrap">
              <Field name="consent" type="checkbox" as={Checkbox}/>
              <p>Your consent is required here</p>
            </div>

            <Field name="register" type="radio" as={Radio}/>
            <Field name="register" type="radio" as={Radio}/>

            <Button type="submit" variant="contained">SUBMIT</Button>
            <pre>
              {JSON.stringify(values, null, 2)}
            </pre>
          </Form>
        )}
      </Formik>
    </div>
  )
};
