import React, { useState } from 'react';

import { TextField, Checkbox } from '@material-ui/core';
import { RadioButtonsGroup } from './RadioGroup';

export const Form = () => {
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    gender: 'female',
    checked: true
  });

  const handleChange = event => {
    const {name, value, checked} = event.target;
    console.log(event);
    setValues({
      ...values,
    [name]: value,
      checked
    })
  };

  return (
    <form noValidate autoComplete="off">
      <div className="form">
        <TextField
          required
          name="name"
          label="Name"
          // helperText="Some important text"
          variant="outlined"
          value={values.name}
          onChange={handleChange}
        />
        <TextField
          required
          name="email"
          label="Email"
          // helperText="Some important text"
          variant="outlined"
          value={values.email}
          onChange={handleChange}
        />
        <TextField
          required
          name="password"
          label="Password"
          type="password"
          variant="outlined"
          value={values.password}
          onChange={handleChange}
        />
        {/*<RadioButtonsGroup*/}
        {/*  value={values.gender}*/}
        {/*  onChange={handleChange}*/}
        {/*/>*/}
        <Checkbox
          checked={values.checked}
          onChange={handleChange}
          value="secondary"
          color="primary"
        />
      </div>
    </form>
  );
};
