import React from 'react';
import {Formik, Field, Form, useField} from 'formik';
import {Button, TextField, Checkbox, FormControlLabel, Radio} from "@material-ui/core";

const CustomRadio = ({label, ...props}) => {
  const [field] = useField(props);

  return (
    <FormControlLabel {...field} control={<Radio/>} label={label}/>
  )
};

export const FormikForm2 = () => {
  return (
    <div>
      <Formik initialValues={{
        name: "",
        email: "",
        password: "",
        consent: true,
        auth: "authorized",

      }} onSubmit={(data, {setSubmitting}) => {
        setSubmitting(true);
        //async call
        console.log(data);
        setSubmitting(false);
      }}
      >
        {({values, isSubmitting}) => (
          <Form className="form">
            <Field
              name="name"
              type="input"
              autoComplete="off"
              variant="outlined"
              label="Name"
              as={TextField}
            />
            <Field
              name="email"
              type="input"
              autoComplete="off"
              variant="outlined"
              label="Email"
              as={TextField}
            />
            <Field
              name="password"
              type="password"
              autoComplete="off"
              variant="outlined"
              label="Password"
              as={TextField}
            />
            <div className="single-checkbox-wrap">
              <Field name="consent" type="checkbox" as={Checkbox}/>
              <p>Your consent is required here</p>
            </div>
            <div className="radio-group">
              <CustomRadio name="auth" type="radio" value="authorized" label="authorised"/>
              <CustomRadio name="auth" type="radio" value="unauthorized" label="unauthorised"/>
            </div>
            <Button type="submit" variant="contained">SUBMIT</Button>
            <pre>
              {JSON.stringify(values, null, 2)}
            </pre>
          </Form>
        )}
      </Formik>
    </div>
  )
};
