  // }

  return (
    <form onSubmit={handleSubmit} noValidate>
      <div className="form">
        <TextField
          required
          name="name"
          label="Name"
          autoComplete="off"
          // helperText="Some important text"
          variant="outlined"
          value={values.name}
          onChange={handleChange}
        />
        {/*<RadioButtonsGroup*/}
        {/*  value={values.gender}*/}
        {/*  onChange={handleChange}*/}
        {/*/>*/}
        <TextField
          required
          name="email"
          label="Email"
          autoComplete="off"
          // helperText="Some important text"
          variant="outlined"
          value={values.email}
          onChange={handleChange}
        />
        <TextField
          required
          name="password"
          label="Password"
          autoComplete="off"
          type="password"
          variant="outlined"
          value={values.password}
          onChange={handleChange}
        />
        <div className="form-checkbox">
          <Checkbox
            checked={checked}
            onChange={handleCheck}
            value="secondary"
            color="primary"
          />
          <p>A checkbox is a GUI widget that permits the user to make a binary choice</p>
        </div>
        <Button type="submit" variant="contained">SUBMIT</Button>
      </div>
    </form>
  );
};
