import React from 'react';
import {FormControl, FormLabel, FormGroup, FormControlLabel, Checkbox} from '@material-ui/core';

export const CheckboxesGroup = () => {
  const [state, setState] = React.useState({
    offers: false,
    news: false
  });

  const handleChange = name => event => {
    setState({ ...state, [name]: event.target.checked });
  };

  const { offers, news } = state;

  return (
    <div>
      <FormControl component="fieldset">
        <FormLabel component="legend">Choose options</FormLabel>
        <FormGroup>
          <FormControlLabel
            control={<Checkbox checked={offers} onChange={handleChange('offers')} value="offers" />}
            label="Do you want to know about our special offers?"
          />
          <FormControlLabel
            control={<Checkbox checked={news} onChange={handleChange('news')} value="news" />}
            label="What about news from us?"
          />
        </FormGroup>
      </FormControl>
    </div>
  );
}
