import React, { useState } from 'react';

import { TextField } from "@material-ui/core";

export const Form = () => {
  const [values, setValues] = useState({
    name: "",
    email: "",
    password: ""
  });

  const handleChange = event => {
    const {name, value} = event.target;

    setValues({
      ...values,
    [name]: value
    })
  };

  return (
    <form noValidate autoComplete="off">
      <div className="form">
        <TextField
          required
          name="name"
          label="Name"
          // helperText="Some important text"
          variant="outlined"
          value={values.name}
          onChange={handleChange}
        />
        <TextField
          required
          name="email"
          label="Email"
          // helperText="Some important text"
          variant="outlined"
          value={values.email}
          onChange={handleChange}
        />
        <TextField
          required
          name="password"
          label="Password"
          type="password"
          variant="outlined"
          value={values.password}
          onChange={handleChange}
        />
      </div>
    </form>
  );
};
