import React from 'react';
import {Formik, Field, Form, useField} from 'formik';
import {Button, TextField, Checkbox, FormControlLabel, Radio} from "@material-ui/core";
import {validationSchema} from '../validation/validationSchema';

const CustomRadio = ({label, ...props}) => {
  const [field] = useField(props);
  return (
    <FormControlLabel {...field} control={<Radio/>} label={label}/>
  )
};

const CustomTextField = ({variant, label, autoComplete, ...props}) => {
  const [field, meta] = useField(props);
  return (
    <TextField {...field} variant="outlined" label={label} autoComplete="off"/>
  )
};

export const FormikForm2 = () => {
  return (
    <div>
      <Formik initialValues={{
        name: "",
        email: "",
        password: "",
        consent: true,
        auth: "authorized",
      }} validate={validationSchema} onSubmit={(data, {setSubmitting}) => {
        setSubmitting(true);
        //async call
        console.log(data);
        setSubmitting(false);
      }}
      >
        {({values, isSubmitting}) => (
          <Form className="form">
            <CustomTextField
              name="name"
              type="input"
              label="Name"
            />
            <CustomTextField
              name="email"
              type="input"
              label="Email"
            />
            <CustomTextField
              name="password"
              type="password"
              label="Password"
            />
            <div className="single-checkbox-wrap">
              <Field name="consent" type="checkbox" as={Checkbox}/>
              <p>Your consent is required here</p>
            </div>
            <div className="radio-group">
              <CustomRadio name="auth" type="radio" value="authorized" label="authorised"/>
              <CustomRadio name="auth" type="radio" value="unauthorized" label="unauthorised"/>
            </div>
            <Button type="submit" variant="contained">SUBMIT</Button>
            <pre>
              {JSON.stringify(values, null, 2)}
              {JSON.stringify(errors, null, 2)}
            </pre>
          </Form>
        )}
      </Formik>
    </div>
  )
};
