import React, {Fragment} from 'react';
import {useFormik} from 'formik';
import {validationSchema} from '../validation/validationSchema';

import {Button, TextField} from "@material-ui/core";
import {CheckboxesGroup} from '../components/CheckBoxGroup';

export const FormikForm = () => {
  const {handleChange, handleSubmit, values, errors} = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      options: ['offers']
    },
    validationSchema,
    onSubmit(values) {
      console.log(values)
    }
  });

  return (
    <Fragment>
      <h1 className="page-title">Form Validation with Formik</h1>
      <form onSubmit={handleSubmit} noValidate className="form">
        <TextField
          required
          onChange={handleChange}
          values={values.name}
          {...errors.name && {helperText: errors.name, error: true}}
          name="name"
          label="Name"
          autoComplete="off"
          variant="outlined"
        />
          <TextField
            required
            onChange={handleChange}
            values={values.email}
            {...errors.email && {helperText: errors.email, error: true}}
            name="email"
            label="Email"
            autoComplete="off"
            variant="outlined"
          />
        <TextField
          required
          onChange={handleChange}
          values={values.email}
          {...errors.password && {helperText: errors.password, error: true}}
          name="password"
          label="Password"
          autoComplete="off"
          variant="outlined"
        />
        <CheckboxesGroup onChange={handleChange} values={values.options}/>
          <Button type="submit" variant="contained">SUBMIT</Button>
    </form>
    </Fragment>
  )
};
