import React, {Fragment, useEffect} from 'react';
import {useFormik, Field} from 'formik';
import {validationSchema} from '../validation/validationSchema';

import {Button, TextField, FormControlLabel, Checkbox} from "@material-ui/core";

export const FormikForm = () => {
  const {handleChange, handleSubmit, values, errors} = useFormik({
    initialValues: {
      name: '',
      email: '',
      password: '',
      isEnabled: false,
    },
    validationSchema,
    onSubmit(values) {
      console.log(values)
    }
  });

  useEffect(()=> {
    if(Object.keys(errors).length === 0 && isSubmitting) {
      console.log(values)
    }
  }, [errors]);

  return (
    <Fragment>
      <h1 className="page-title">Form Validation with Formik</h1>
      <form onSubmit={handleSubmit} noValidate className="form">
        <TextField
          required
          onChange={handleChange}
          values={values.name}
          {...errors.name && {helperText: errors.name, error: true}}
          name="name"
          label="Name"
          autoComplete="off"
          variant="outlined"
        />
        <TextField
          required
          onChange={handleChange}
          values={values.email}
          {...errors.email && {helperText: errors.email, error: true}}
          name="email"
          label="Email"
          autoComplete="off"
          variant="outlined"
        />
        <TextField
          required
          onChange={handleChange}
          values={values.email}
          {...errors.password && {helperText: errors.password, error: true}}
          name="password"
          label="Password"
          autoComplete="off"
          variant="outlined"
        />
        <FormControlLabel
          control={<Checkbox
            checked={values.isEnabled}
            onChange={handleChange("isEnabled")}
            name="isEnabled"
          />} label="This is a consent"
        />
        {errors.isEnabled && <div className="form-field-error">{errors.isEnabled}</div>}
        <Button type="submit" variant="contained">SUBMIT</Button>
      </form>
    </Fragment>
  )
};
